import React, { Component } from 'react';
import 'react-mdl/extra/material.css';
import 'react-mdl/extra/material.js';
import me from '../images/foto.jpg';
import { Grid, Cell } from 'react-mdl';
import Hero from './Hero';
import { FaFacebook } from 'react-icons/fa';
export default class AboutMe extends Component {
    render() {
        return (
          <Hero>
            <div style={{width: '100%', margin: 'auto'}}>
        <Grid className="aboutMe">
          <Cell col={12}>
            <img
              src={me}
              alt="avatar"
              className="avatar-img"
              />

            <div className="banner-text">
              <h3>Merhaba, ben Oğuzhan Dursun!</h3>
              <h5>Ankara'da yaşıyorum, Ankaralıyım ve 21 yaşındayım. Hacettepe Üniversitesi Bilgisayar Mühendisliği bölümünde 2.sınıfı bitirdim. Arkadaşlarımla vakit geçirmeyi, futbol oynamayı ve gezmeyi severim. 2019 yılında üniversitemin topluluklarından IEEE Hacettepe'de 135 kişilik Python dersinde eğitmenlik yaptım ve şu an(2020 yaz) Tübitak Bilgem YTE'de staj yapıyorum.  </h5>
              <h6>GPA: 3.51/4.00</h6>
              <h6><u>Yetenekler</u></h6>
            <hr/> 
          <p>İşletim Sistemleri: Windows | Linux</p>
          <p>Programlama Dilleri: Python | Java | C | C# | Dart | JavaScript</p>
          <p>Backend Teknolojileri: Spring Boot</p>
          <p>Frontend Teknolojileri: HTML/CSS | Bootstrap | React </p>
          <p>Veritabanı: PostgreSQL </p>
            </div>
          </Cell>
        </Grid>
      </div>
      </Hero>
        )
    }
}
